mapper.select:
select 

from file_ownership
where file_id = #{fileId,jdbcType=BIGINT}
and user_id = #{userId,jdbcType=BIGINT}

mapper.delete:
delete from file_ownership 
where 1 = 1

    and ownership_id = #{ownershipId,jdbcType=BIGINT}


    and file_id = #{fileId,jdbcType=BIGINT}


    and user_id = #{userId,jdbcType=BIGINT}
